/**
 * Create an object with the same keys as the provided object and values generated by running the provided function for each value
 */

function mapValues(obj, fn) {
  return Object.keys(obj).reduce((acc, k) => {
    acc[k] = fn(obj[k], k, obj)
    return acc
  }, {})
}

const users = {
  fred: { f_name: 'fred', l_name: 'whoom', age: 30 },
  pebbles: { f_name: 'pebbles', l_name: 'sefrat', age: 10 },
}

console.log(mapValues(users, u => u.age))
